window.onload=()=>{const e=document.querySelectorAll("nav a:not(.inline-block)");e.forEach(()=>addEventListener("click",scrollToLocation))};function scrollToLocation(e){const t=e.target.getAttribute("href");if(t&&t[0]==="#"){e.preventDefault();const n=document.querySelector(t).offsetTop,s=window.innerHeight;scroll({top:n-s*.05,behavior:"smooth"})}}